mapper.delete:
delete
from letsfutsal.team_member
where team_id = #{teamId}
  and user_id = #{userId}

mapper.select:
select t.team_id as teamId, t.team_name as teamName, t.region as region, sum(u.point) as totalPoints, avg(u.point) as averagePoints, count(tm.user_id) as memberCount from letsfutsal.team t join letsfutsal.team_member tm on t.team_id = tm.team_id join letsfutsal.user u on tm.user_id = u.user_id
where t.min_grade <= #{grade} and t.max_grade >= #{grade}
group by t.team_id, t.team_name, t.region order by averagePoints desc, t.team_id limit 10
